import {map, ObservableMap, observable} from 'mobx';

export class BookmarkViewModel {
  @observable
  public inputElementSize: number;

  private _editableBookmarks: ObservableMap<void>;

  public constructor(inputElementSize: number = 30) {
    this.inputElementSize = inputElementSize;
    this._editableBookmarks = map<void>();
  }

  public get editableBookmarks(): ObservableMap<void> {
    return this._editableBookmarks;
  }
}
